xmlGlossaryData += '                </div>';
xmlGlossaryData += '                <div class=\"GlossaryPageDefinition\" id=\"MCDropDownBody_855183615_72\" style=\"display: none;\">The average maximum vertical extension of flames at the leading edge of the fire front. Occasional flashes that rise above the general level of flames are not considered. This distance is less than the flame length if flames are tilted due to wind or slope. (From http://www.nwcg.gov/pms/pubs/glossary/f.htm.)</div>';
xmlGlossaryData += '            </div>';
xmlGlossaryData += '            <div class=\"GlossaryPageEntry\">';
xmlGlossaryData += '                <div class=\"GlossaryPageTerm\">';
