xmlGlossaryData += '                <div class=\"GlossaryPageDefinition\" id=\"MCDropDownBody_855183615_73\" style=\"display: none;\">The flame length of a spreading surface fire within the flaming front is measured from midway in the active flaming combustion zone to the average tip of the flames. Flame length is an indicator of fire intensity. Flame lengths are divided into classes (low, medium, high, very high). In IFTDSS risk assessment, you can define the lengths you want assigned for each class.</div>';
xmlGlossaryData += '            </div>';
xmlGlossaryData += '            <div class=\"GlossaryPageEntry\">';
xmlGlossaryData += '                <div class=\"GlossaryPageTerm\">';
xmlGlossaryData += '                    <a href=\"javascript:void(0);\" class=\"GlossaryPageTerm\" id=\"MCDropDownHotSpot_855183615_74\" onclick=\"FMCDropDown( this ); FMCScrollToVisible( window, this.parentNode.parentNode ); return false;\">flame length class</a>';
xmlGlossaryData += '                    <a name=\"855183615_anchor75\">';
xmlGlossaryData += '                    </a>';
xmlGlossaryData += '                </div>';
xmlGlossaryData += '                <div class=\"GlossaryPageDefinition\" id=\"MCDropDownBody_855183615_74\" style=\"display: none;\">The flame length of a spreading surface fire within the flaming front is measured from midway in the active flaming combustion zone to the average tip of the flames. Flame length is an indicator of fire intensity. Flame lengths are divided into classes (low, medium, high, very high). In IFTDSS risk assessment, you can define the lengths you want assigned for each class.</div>';
xmlGlossaryData += '            </div>';
xmlGlossaryData += '            <div class=\"GlossaryPageEntry\">';
